mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num, writer, title, viewCount, regdate FROM forum_cafe
ORDER BY num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE forum_cafe
SET viewCount = viewCount + 1
WHERE num = #{num}

UPDATE forum_cafe
SET title = #{title}, imagePath = #{imagePath}, content = #{content}
WHERE num = #{num}
